
def find_num_chars(datastream, num_chars):
    num_windows_seen = 0
    while num_windows_seen+num_chars < len(datastream):
        marker = set(datastream[num_windows_seen:num_windows_seen+num_chars])
        has_duplicates = len(marker) != num_chars
        if not has_duplicates:
            return num_windows_seen + num_chars
        num_windows_seen += 1
    raise Exception('no such marker found')


